    .skillName("타입스크립트")
    .skillImgUrl("https://i.postimg.cc/C50Qnxmj/image.png")
    .build());
skillSelect.add(1);
skillSelect.add(2);
const removeSkill = (num: number) => {
  skillSelectRef.value.delete(num);
}
</script>

